import { usePosts } from "../context/postsContext";
import { useFavorite } from "../context/favoritiesPostsContext";
import { useState } from "react";

export default function useFilterPosts() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedUsers, setSelectedUsers] = useState([]);
  const { favoritePosts, showFavorites, setShowFavorites } = useFavorite();
  const { posts } = usePosts();

  const userOptions = Array.from(
    new Set(posts.map((post) => post.user.name))
  ).map((name) => ({
    label: name,
    value: name,
  }));

  const filterPosts = (posts) => {
    return posts.filter((post) => {
      const titleMatch = post.title
        .toLowerCase()
        .includes(searchTerm.toLowerCase());

      const userMatch =
        selectedUsers.length === 0 ||
        selectedUsers.find((user) => user.value === post.userId);
      const favoriteMatch = showFavorites
        ? favoritePosts.includes(post.id)
        : true;
      return titleMatch && userMatch && favoriteMatch;
    });
  };
  const filteredPosts = filterPosts(posts);

  return {
    filteredPosts,
    userOptions,
    searchTerm,
    selectedUsers,
    setSearchTerm,
    setSelectedUsers,
    setShowFavorites,
  };
}
