import { useMemo, useState } from "react";
import { useFavoriteTodos } from "../context/favoritiesTodosContext";
import { useTodos } from "../context/todosContext";

export function useFilterTodos() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedUsers, setSelectedUsers] = useState([]);
  const { favoriteTodos, showFavorites, setShowFavorites } = useFavoriteTodos();
  const { todos } = useTodos();

  const uniqueUsers = Array.from(
    new Set(todos.map((todo) => (todo.user ? todo.user.id : "Anonymous")))
  ).map((id) => {
    const todo = todos.find((todo) => todo.user && todo.user.id === id);
    return todo ? todo.user : { name: "Anonymous", id: "Anonymous" };
  });

  const userOptions = uniqueUsers.map((user) => ({
    label: user.name,
    value: user.id,
  }));

  const filteredtodos = useMemo(
    () =>
      todos.filter((todo) => {
        const titleMatch = todo.title
          .toLowerCase()
          .includes(searchTerm.toLowerCase());
        const userMatch =
          selectedUsers.length === 0 ||
          selectedUsers.find((user) => user.value === todo.userId);
        const favoriteMatch = showFavorites
          ? favoriteTodos.includes(todo.id)
          : true;
        return titleMatch && userMatch && favoriteMatch;
      }),
    [todos, searchTerm, selectedUsers, favoriteTodos, showFavorites]
  );

  return {
    userOptions,
    searchTerm,
    selectedUsers,
    setSearchTerm,
    setSelectedUsers,
    setShowFavorites,
    filteredtodos,
  };
}
