import axios from "axios";
import { useEffect, useState } from "react";
import { ALBUMS_API, PHOTOS_API, USER_API } from "../const/const";
import _ from "lodash";

export function useGetsPhoto() {
  const [albums, setAlbums] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  const getAlbumsLists = async () => {
    const [albumsResponse, usersResponse, photosResponse] = await Promise.all([
      axios.get(ALBUMS_API),
      axios.get(USER_API),
      axios.get(PHOTOS_API),
    ]);

    return [albumsResponse.data, usersResponse.data, photosResponse.data];
  };

  useEffect(() => {
    setLoading(true);
    getAlbumsLists()
      .then(([albums, users, photos]) => {
        const userMap = _.keyBy(users, "id");
        const photosMap = _.groupBy(photos, "albumId");
        const albumsWithPhotos = albums.map((album) => {
          return {
            ...album,
            user: userMap[album.userId],
            photos: photosMap[album.id],
          };
        });
        setAlbums(albumsWithPhotos);
      })
      .catch((err) => setError(true))
      .finally(() => setLoading(false));
  }, []);

  return { albums };
}
