import React, { useEffect, useState } from "react";
import Layout from "../components/layout/Layout";
import axios from "axios";
import { Button, Card, Form } from "react-bootstrap";
import Pagination from "../components/pagination/Pagination";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);

  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage, setUsersPerPage] = useState(10);
  const lastIndexUser = currentPage * usersPerPage;
  const firstIndexUser = lastIndexUser - usersPerPage;

  const paginate = (number) => setCurrentPage(number);
  const changePage = (value) => {
    setUsersPerPage(value);
  };

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((res) => setPosts(res.data));
  }, []);

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/users")
      .then((res) => setUsers(res.data));
  }, []);

  const processedList = posts.map((post) => {
    const user = users.find((user) => user.id === post.userId);
    if (user) {
      return { ...post, userId: user.name };
    }
    return post;
  });
  const currentUsers = processedList.slice(firstIndexUser, lastIndexUser);

  return (
    <Layout>
      <div className="d-flex bg-white align-items-center p-2 m-1 rounded gap-2">
        <Button>Show</Button>
        <Form.Select
          aria-label="Default select example"
          className="w-auto"
          value={usersPerPage}
          onChange={(event) => changePage(event.target.value)}
        >
          <option value="5">5</option>
          <option value="10">10</option>
          <option value="15">15</option>
          <option value="20">20</option>
        </Form.Select>
      </div>
      <div>
        {currentUsers.map((post) => {
          return (
            <Card key={post.id} className="text-center m-1 p-2 bg-light">
              <div>
                {" "}
                <h3>{post.title}</h3>
                <h5>{post.userId}</h5>
                <p>{post.body}</p>
              </div>
              <div>
                <Button></Button>
                <Button></Button>
                <Button></Button>
                <Button>
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    class="bi bi-chat-dots"
                    viewBox="0 0 16 16"
                  >
                    <path d="M5 8a1 1 0 1 1-2 0 1 1 0 0 1 2 0zm4 0a1 1 0 1 1-2 0 1 1 0 0 1 2 0zm3 1a1 1 0 1 0 0-2 1 1 0 0 0 0 2z" />
                    <path d="m2.165 15.803.02-.004c1.83-.363 2.948-.842 3.468-1.105A9.06 9.06 0 0 0 8 15c4.418 0 8-3.134 8-7s-3.582-7-8-7-8 3.134-8 7c0 1.76.743 3.37 1.97 4.6a10.437 10.437 0 0 1-.524 2.318l-.003.011a10.722 10.722 0 0 1-.244.637c-.079.186.074.394.273.362a21.673 21.673 0 0 0 .693-.125zm.8-3.108a1 1 0 0 0-.287-.801C1.618 10.83 1 9.468 1 8c0-3.192 3.004-6 7-6s7 2.808 7 6c0 3.193-3.004 6-7 6a8.06 8.06 0 0 1-2.088-.272 1 1 0 0 0-.711.074c-.387.196-1.24.57-2.634.893a10.97 10.97 0 0 0 .398-2z" />
                  </svg>
                </Button>
              </div>
            </Card>
          );
        })}
      </div>
      <Pagination
        somethingPerPage={usersPerPage}
        totalSomething={posts.length}
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
        paginate={paginate}
      />
    </Layout>
  );
};

export default Posts;
