import React from "react";
import { SelectToogle } from "./SelectToogle";
import { InputForFilteredByTitle } from "./InputForFilteredByTitle";
import { SelectForFilteredByName } from "./SelectForFilteredByName";
import { FilteringInputByFavoritePosts } from "./FilteringInputByFavoritePosts";
import { Button } from "react-bootstrap";
import { useLocation } from "react-router-dom";
import { FilterByCompleted } from "../filterComponents/FilterByCompleted";

export const PostsFilterMenu = ({ setShowModalForAddNewPost }) => {
  const location = useLocation();
  return (
    <div className="d-flex bg-white justify-content-between align-items-center p-3 m-1 rounded gap-3">
      <div className="d-flex flex-row align-items-center m-1 gap-3">
        <SelectToogle />
        <InputForFilteredByTitle />
        {location.pathname === "/todos" ? null : <SelectForFilteredByName />}
        <FilteringInputByFavoritePosts />
        {location.pathname === "/todos" && <FilterByCompleted />}
      </div>
      {location.pathname === "/posts" && (
        <Button
          onClick={() => {
            setShowModalForAddNewPost((prev) => !prev);
          }}
        >
          Add new Post
        </Button>
      )}
    </div>
  );
};
