import React, { useState } from "react";
import { Card, Form } from "react-bootstrap";
import {
  ButtonToAddToFavorites,
  CommentButton,
  DeleteButton,
  EditButton,
} from "../../utilities/const/Buttons";

import styles from "../../pages/Posts.module.scss";
import FormForUpdatePost from "../formForUpdatePost/FormForUpdatePost";
import useSaveForm from "../../utilities/hooks/useSaveForm";

export const PostCard = ({
  post,
  favoritePosts,
  selectedComments,
  selectedPostForComments,
  selectedPosts,
  handleButtonClick,
  setSelectedPosts,
  handleFavorite,
  handleComment,
  setIsVisabilityModal,
}) => {
  const [editingPost, setEditingPost] = useState(null);
  const [editedPost, setEditedPost] = useState({
    title: post.title,
    body: "",
    userName: "",
  });
  const { saveData } = useSaveForm({
    setEditedPost,
    editingPost,
    setEditingPost,
    editedPost,
    posts: [],
    setPosts: () => null,
  });

  const handleSave = async () => {
    saveData();
  };

  const [isEdit, setIsEdit] = useState(false);
  const handleCloseEdit = () => setIsEdit(false);

  const handleEdit = (post) => {
    setIsEdit(true);
  };
  if (isEdit) {
    return (
      <div
        key={post.id}
        className="d-flex flex-column bg-light p-3 m-1 rounded"
      >
        <FormForUpdatePost
          editedPost={editedPost}
          setEditedPost={setEditedPost}
          handleSave={handleSave}
          handleCancel={handleCloseEdit}
        />
      </div>
    );
  }
  return (
    <Card
      className={`text-center m-1 p-2 ${
        favoritePosts.includes(post.id) ? "bg-warning" : "bg-light"
      }`}
    >
      <div>
        <Form.Check
          className={styles.check}
          checked={selectedPosts.includes(post.id)}
          onChange={(e) => {
            if (e.target.checked) {
              setSelectedPosts([...selectedPosts, post.id]);
            } else {
              setSelectedPosts(
                selectedPosts.filter((postId) => postId !== post.id)
              );
            }
          }}
        />

        <h3>{post.title}</h3>
        <h5>{post.userName}</h5>
        <p>{post.body}</p>
      </div>

      <div className="d-flex justify-content-center gap-5">
        <EditButton onClick={handleEdit} />
        <ButtonToAddToFavorites onClick={() => handleFavorite(post.id)} />
        <DeleteButton
          onClick={() => {
            handleButtonClick(post.id);
            setIsVisabilityModal(true);
          }}
        />
        <CommentButton
          className={
            selectedPostForComments === post.id
              ? styles.commentButtonActive
              : ""
          }
          onClick={() => {
            handleComment(post.id);
          }}
        />
      </div>
      {selectedPostForComments === post.id &&
        selectedComments.map((comments) => {
          return (
            <div
              key={comments.id}
              className="m-1 p-2 bg-info
            rounded"
            >
              <h4>{comments.email}</h4>
              <h5>{comments.name}</h5>
              <p>{comments.body}</p>
            </div>
          );
        })}
    </Card>
  );
};
