import React, { useState } from "react";
import { Layout } from "../components/layout/Layout";
import { useGetsPhoto } from "../utilities/hooks/useGetsPhoto";

import { usePagination } from "../utilities/hooks/usePagination";
import { AlbumsList } from "../components/albumsComponents/AlbumsList";
import { Pagination } from "../components/pagination/Pagination";
import { FilteredMain } from "../components/postComponents/FilteredMain";

import styles from "./Posts.module.scss";
import { Button } from "react-bootstrap";
import { ModalForAddNewPost } from "../components/postComponents/ModalForAddNewPost";
import { ModalWindowByDeleteAllCheckedPosts } from "../components/modalsWindow/modalWindowForDelete/ModalWindowByDeleteAllCheckedPosts";
import { ModalForDelete } from "../components/modalsWindow/modalWindowForDelete/ModalWindowByDelete";
import { ModalWindowAddToFavorites } from "../components/modalsWindow/addToFavoritesWindow/ModalWindowAddToFavorites";

const Photo = () => {
  const { albums } = useGetsPhoto();
  const [isVisabilityModalOfFavorities, setIsVisabilityModalOfFavorities] =
    useState(false);
  const [showModalDeletingCheckedPosts, setShowModalDeletingCheckedPosts] =
    useState(false);
  const [showModalForAddNewPost, setShowModalForAddNewPost] = useState(false);
  const [isVisabilityModalOfDelete, setIsVisabilityModalOfDelete] =
    useState(false);
  const [selectedAlbumsId, setSelectedAlbumsId] = useState(null);
  const [selectedAlbums, setSelectedAlbums] = useState([]);
  const { deleteAlbums } = useDeletePostsFromSelect(setSelectedAlbums);

  const {
    postsPerPage,
    changePage,
    setCurrentPage,
    currentPage,
    paginate,
    currentLists,
  } = usePagination({ list: albums });

  return (
    <Layout>
      <ModalWindowAddToFavorites
        selectedPosts={selectedAlbums}
        // handleAddMultiplyToFavoritiesPosts={handleAddMultiplyToFavoritiesPosts}
        isVisabilityModalOfFavorities={isVisabilityModalOfFavorities}
        setIsVisabilityModalOfFavorities={setIsVisabilityModalOfFavorities}
      />
      <ModalForDelete
        id={selectedAlbumsId}
        isVisabilityModalOfDelete={isVisabilityModalOfDelete}
        // deletePost={deletePost}
        setIsVisabilityModalOfDelete={setIsVisabilityModalOfDelete}
      />
      <ModalWindowByDeleteAllCheckedPosts
        selectedPosts={selectedAlbums}
        onHide={setShowModalDeletingCheckedPosts}
        show={showModalDeletingCheckedPosts}
        deleteAlbums={deleteAlbums}
        setSelectedPosts={setSelectedAlbums}
      />
      <ModalForAddNewPost
        showModalForAddNewPost={showModalForAddNewPost}
        setShowModalForAddNewPost={setShowModalForAddNewPost}
      />
      <FilteredMain
        // setShowModalForAddNewPost={setShowModalForAddNewPost}
        postsPerPage={postsPerPage}
        changePage={changePage}
        // searchTerm={searchTerm}
        // setSearchTerm={setSearchTerm}
        // userOptions={userOptions}
        // setSelectedUsers={setSelectedUsers}
        // setShowFavorites={setShowFavorites}
        // showFavorites={showFavorites}
      />
      <div className={styles.container}>
        <AlbumsList
          setIsVisabilityModalOfDelete={setIsVisabilityModalOfDelete}
          // deletePost={deletePost}
          posts={currentLists}
          setSelectedAlbums={setSelectedAlbums}
          selectedAlbums={selectedAlbums}
          setSelectedAlbumsId={setSelectedAlbumsId}
        />
      </div>
      {selectedAlbums.length > 0 && (
        <div className="d-flex justify-content-center gap-5">
          <Button onClick={() => setShowModalDeletingCheckedPosts(true)}>
            Delete
          </Button>
          <Button onClick={() => setIsVisabilityModalOfFavorities(true)}>
            Add
          </Button>
        </div>
      )}
      <Pagination
        somethingPerPage={postsPerPage}
        totalSomething={albums.length}
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
        paginate={paginate}
      />
    </Layout>
  );
};

export default Photo;
