import React from "react";
import {
  ButtonToAddToFavorites,
  CommentButton,
  DeleteButton,
  EditButton,
} from "../../utilities/const/Buttons";
import styles from "./Posts.module.scss";

import { Card, Form } from "react-bootstrap";
export const AlbumsCard = ({
  post,
  selectedAlbums,
  setSelectedAlbums,
  setSelectedAlbumsId,
}) => {
  return (
    <Card>
      <div>
        <Form.Check
          className={styles.check}
          checked={selectedAlbums.includes(post.id)}
          onChange={(e) => {
            if (e.target.checked) {
              setSelectedAlbums([...selectedAlbums, post.id]);
            } else {
              setSelectedAlbums(
                selectedAlbums.filter((postId) => postId !== post.id)
              );
            }
          }}
        />

        <h3>{post?.title}</h3>
        <h5>{post?.user?.name}</h5>
      </div>
    </Card>
  );
};
