import axios from "axios";
import { toast } from "react-hot-toast";
import { useFavorite } from "../context/favoritiesPostsContext";
import useDownloadPosts from "./useDownloadPosts";

export const useDeletePostsFromFavorities = (setSelectedPosts) => {
  const { setPosts } = useDownloadPosts();
  const { favoritePosts, setFavoritePosts } = useFavorite();
  const deletePosts = async (selectedPosts) => {
    const postsToDelete = [...selectedPosts];

    const deleteRequests = postsToDelete.map((postId) =>
      axios.delete(`https://jsonplaceholder.typicode.com/posts/${postId}`)
    );
    try {
      await Promise.all(deleteRequests);

      setPosts((prevPosts) =>
        prevPosts.filter((post) => !postsToDelete.includes(post.id))
      );

      setFavoritePosts((prevFavoritePosts) =>
        prevFavoritePosts.filter((postId) => !postsToDelete.includes(postId))
      );

      setSelectedPosts((prevSelectedPosts) =>
        prevSelectedPosts.filter((postId) => !postsToDelete.includes(postId))
      );

      localStorage.setItem("favoritePosts", JSON.stringify(favoritePosts));

      toast.success("Selected posts removed");
    } catch (error) {
      toast.error("Failed to delete some posts");
    }
  };

  return { deletePosts };
};
