import { Button, Modal } from "react-bootstrap/";
import AddPostForm from "./AddNewPostForm";

export const ModalForAddNewPost = ({
  setShowModalForAddNewPost,
  showModalForAddNewPost,
}) => {
  if (!showModalForAddNewPost) {
    return null;
  }
  return (
    <div>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          zIndex: 1040,
        }}
      ></div>
      <div
        className="modal show"
        style={{ display: "block", position: "fixed", zIndex: 1050 }}
      >
        <Modal.Dialog>
          <Modal.Header
            onClick={() => setShowModalForAddNewPost(false)}
          ></Modal.Header>
          <Modal.Body>
            <AddPostForm />
          </Modal.Body>
          <Modal.Footer>
            <Button
              variant="secondary"
              onClick={() => {
                setShowModalForAddNewPost(false);
              }}
            >
              NO
            </Button>
            <Button
              variant="primary"
              onClick={() => {
                setShowModalForAddNewPost(false);
              }}
            >
              YES
            </Button>
          </Modal.Footer>
        </Modal.Dialog>
      </div>
    </div>
  );
};
