import axios from "axios";
import { useEffect, useState } from "react";
import { COMMENT_API, POST_API, USER_API } from "../const/const";
import _ from "lodash";
import { useDeletePost } from "./useDeletePost";

export default function useGetPosts() {
  const [posts, setPosts] = useState([]);
  const { deletePost } = useDeletePost(setPosts);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const [favoritePosts, setFavoritePosts] = useState(
    JSON.parse(localStorage.getItem("favoritePosts")) || []
  );
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [showFavorites, setShowFavorites] = useState(false);
  console.log("posts", posts);
  const editUser = (id, data) => {
    setPosts((prev) => {
      return prev.map((post) => {
        const { userName, ...rest } = data;
        if (post.id === id) {
          return { ...post, ...rest, user: { ...post.user, name: userName } };
        }
        return post;
      });
    });
  };
  const getPostList = async () => {
    return await Promise.all([
      axios.get(POST_API),
      axios.get(USER_API),
      axios.get(COMMENT_API),
    ]);
  };

  useEffect(() => {
    setLoading(true);
    getPostList()
      .then(([posts, users, comments]) => {
        const usersMap = _.keyBy(users.data, "id");
        const commentsMap = _.groupBy(comments.data, "postId");
        const postsWithCommentsAndUsers = posts.data.map((post) => {
          return {
            ...post,
            user: usersMap[post.userId],
            comments: commentsMap[post.id],
          };
        });

        setPosts(postsWithCommentsAndUsers);
      })
      .catch((err) => setError(true))
      .finally(() => setLoading(false));
  }, []);

  // const userOptions = posts.map((post) => ({
  //   value: post.id,
  //   label: post.user.name,
  // }));
  // console.log("userOptions", userOptions);

  // const filteredPosts = posts.filter(
  //   (post) =>
  //     post.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
  //     (selectedUsers.length === 0 ||
  //       selectedUsers.find((user) => user.value === post.userId)) &&
  //     (!showFavorites || favoritePosts.includes(post.id))
  // );

  return {
    posts,
    setLoading,
    error,
    deletePost,
    setPosts,
    editUser,
    // filteredPosts,
  };
}
