import { useMemo, useState } from "react";
import { useFavoriteTodos } from "../context/favoritiesTodosContext";
import { useTodos } from "../context/todosContext";

export function useFilterTodos() {
  const [searchTerm, setSearchTerm] = useState("");
  const { favoriteTodos, showFavorites, setShowFavorites } = useFavoriteTodos();
  const { todos } = useTodos();

  useEffect(() => {
    const sorted = todos.slice().sort((a, b) => {
      if (a.completed && !b.completed) {
        return 1; // перемещаем выполненную задачу вниз
      } else if (!a.completed && b.completed) {
        return -1; // перемещаем невыполненную задачу вверх
      } else {
        return 0; // сохраняем текущий порядок для задач с одинаковым статусом
      }
    });
    setSortedTodos(sorted);
  }, [todos]);

  const filteredTodos = useMemo(
    () =>
      sortedTodos.filter((todo) => {
        const titleMatch = todo.title
          .toLowerCase()
          .includes(searchTerm.toLowerCase());
        const userMatch =
          selectedUsers.length === 0 ||
          selectedUsers.find((user) => user.value === todo.userId);
        const favoriteMatch = showFavorites
          ? favoriteTodos.includes(todo.id)
          : true;
        return titleMatch && userMatch && favoriteMatch;
      }),
    [sortedTodos, searchTerm, selectedUsers, favoriteTodos, showFavorites]
  );

  return {
    searchTerm,
    setSearchTerm,
    setShowFavorites,
    filteredTodos,
  };
}
