import axios from "axios";
import { useEffect, useState } from "react";
import { COMMENT_API } from "../const/const";

export default function useDownloadComments() {
  const [comments, setComments] = useState([]);

  useEffect(() => {
    axios.get(COMMENT_API).then((res) => setComments(res.data));
  }, []);
  return { comments };
}
