import axios from "axios";
import { useEffect, useState } from "react";
import { COMMENT_API, POST_API, USER_API } from "../const/const";
import _ from "lodash";
export default function useDownloadPosts() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const getPostList = async () => {
    return await Promise.all([
      axios.get(POST_API),
      axios.get(USER_API),
      axios.get(COMMENT_API),
    ]);
  };

  useEffect(() => {
    setLoading(true);
    getPostList()
      .then(([posts, users, comments]) => {
        const usersMap = _.keyBy(users.data, "id");
        const commentsMap = _.keyBy(comments.data, "postId");
        const postsWithCommentsAndUsers = posts.data.map((post) => {
          return {
            ...post,
            user: usersMap[post.userId],
            comments: commentsMap[post.id],
          };
        });
      })
      .catch((err) => setError(true))
      .finally(() => setLoading(false));
  }, []);

  return { posts, setLoading, error };
}
