import React from "react";

import { Layout } from "../components/layout/Layout";
import { useAlbums } from "../utilities/context/albumsContext";
import { useParams } from "react-router-dom";
import { Card, ListGroup } from "react-bootstrap";

const AlbumPhotos = () => {
  const { albumId } = useParams();
  const { albums } = useAlbums();
  const photos = albums.filter((album) => {
    if (album.id === +albumId) {
      return { album };
    }
  });

  console.log("!!!!!!!", photos);
  return (
    <Layout>
      <h2 className="text-center">Album Photos</h2>
      <div>
        {photos.map((photo) => (
          <Card key={photo.id} style={{ width: "18rem" }}>
            <Card.Img variant="top" src={photos.thumbnailUrl} />{" "}
            <Card.Body>
              <Card.Title>{photo.title}</Card.Title>
            </Card.Body>
            <ListGroup className="list-group-flush"></ListGroup>
            <Card.Body>
              <Card.Link href="#">Card Link</Card.Link>
              <Card.Link href="#">Another Link</Card.Link>
            </Card.Body>
          </Card>
        ))}
      </div>
    </Layout>
  );
};

export default AlbumPhotos;
