import React, { useEffect, useState } from "react";
import Layout from "../components/layout/Layout";
import axios from "axios";
import { Button, Card, Form } from "react-bootstrap";
import Pagination from "../components/pagination/Pagination";

import styles from "./Posts.module.scss";
import usePagination from "../utilities/hooks/usePagination";
import { toast } from "react-hot-toast";
import ModalForDelete from "../components/modalWindowForDelete/ModalWindowByDelete";
import {
  ButtonToAddToFavorites,
  CommentButton,
  DeleteButton,
  EditButton,
} from "../utilities/const/Buttons";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);
  const [comments, setComments] = useState([]);
  const [isVisabilityModal, setIsVisabilityModal] = useState(false);
  const [selectedPostId, setSelectedPostId] = useState(null);
  const [selectedComments, setSelectedComments] = useState([]);
  const [isVisabilityComments, setIsVisabilityComments] = useState(false);
  const [selectedPostForComments, setSelectedPostForComments] = useState(null);

  const {
    postsPerPage,
    changePage,
    setCurrentPage,
    currentPage,
    paginate,
    currentPosts,
  } = usePagination({ users, posts });

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((res) => setPosts(res.data));
  }, []);

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/comments")
      .then((res) => setComments(res.data));
  }, []);
  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/users")
      .then((res) => setUsers(res.data));
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`https://jsonplaceholder.typicode.com/posts/${id}`);
      setPosts(posts.filter((post) => post.id !== id));
      toast.success("post deleted");
    } catch (error) {
      toast.error("post not deleted");
    }
  };
  const handleButtonClick = (postId) => {
    setSelectedPostId(postId);
  };

  const handleComment = (userId) => {
    const commentsForUser = comments.filter(
      (comment) => comment.postId === userId
    );
    setSelectedComments(commentsForUser);
    setSelectedPostForComments(postId);
  };
  console.log(selectedComments);
  return (
    <Layout>
      <ModalForDelete
        id={selectedPostId}
        isVisabilityModal={isVisabilityModal}
        handleDelete={handleDelete}
        setIsVisabilityModal={setIsVisabilityModal}
      />
      <div className="d-flex bg-white align-items-center p-2 m-1 rounded gap-2">
        <Button>Show</Button>
        <Form.Select
          aria-label="Default select example"
          className="w-auto"
          value={postsPerPage}
          onChange={(event) => changePage(event.target.value)}
        >
          <option value="10">10</option>
          <option value="20">20</option>
          <option value="50">50</option>
          <option value="100">100</option>
        </Form.Select>
      </div>
      <div className={styles.container}>
        {currentPosts.map((post) => {
          return (
            <Card key={post.id} className="text-center m-1 p-2 bg-light ">
              <div>
                <Form.Check />
                <h3>{post.title}</h3>
                <h5>{post.userName}</h5>
                <p>{post.body}</p>
              </div>

              <div className="d-flex justify-content-center gap-5">
                <EditButton />
                <ButtonToAddToFavorites />
                <DeleteButton
                  onClick={() => {
                    handleButtonClick(post.id);
                    setIsVisabilityModal(true);
                  }}
                />
                <CommentButton
                  onClick={() => {
                    handleComment(post.userId);
                    setIsVisabilityComments(true);
                  }}
                />
              </div>
              {selectedPostForComments === post.id &&
                selectedComments.map((comments) => {
                  return (
                    <div key={comments.id}>
                      <h4>{comments.email}</h4>
                      <h5>{comments.name}</h5>
                      <p>{comments.body}</p>
                    </div>
                  );
                })}
            </Card>
          );
        })}
      </div>
      <Pagination
        somethingPerPage={postsPerPage}
        totalSomething={posts.length}
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
        paginate={paginate}
      />
    </Layout>
  );
};

export default Posts;
