import React, { useEffect, useState } from "react";
import Layout from "../components/layout/Layout";
import axios from "axios";
import { Card, Form } from "react-bootstrap";
import Pagination from "../components/pagination/Pagination";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);

  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage, setUsersPerPage] = useState(10);
  const lastIndexUser = currentPage * usersPerPage;
  const firstIndexUser = lastIndexUser - usersPerPage;
  const currentUsers = posts.slice(firstIndexUser, lastIndexUser);
  const paginate = (number) => setCurrentPage(number);
  const changePage = (value) => {
    setUsersPerPage(value);
  };

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((res) => setPosts(res.data));
  }, []);

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/users")
      .then((res) => setUsers(res.data));
  }, []);

  const processedList = posts.map((post) => {
    const user = users.find((user) => user.id === post.userId);
    if (user) {
      return { ...post, userId: user.name };
    }
    return post;
  });

  return (
    <Layout>
      <div className="d-flex bg-white align-items-center">
        <button>Show</button>
        <Form.Select
          aria-label="Default select example"
          className={cn(styles.numberInp)}
          value={usersPerPage}
          onChange={(event) => changePage(event.target.value)}
        >
          <option value="5">5</option>
          <option value="10">10</option>
          <option value="15">15</option>
          <option value="20">20</option>
        </Form.Select>
      </div>
      <div>
        {processedList.map((post) => {
          return (
            <Card key={post.id}>
              <h3>{post.title}</h3>
              <h4>{post.userId}</h4>
              <p>{post.body}</p>
            </Card>
          );
        })}
      </div>
      <Pagination
        somethingPerPage={usersPerPage}
        totalSomething={posts.length}
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
        paginate={paginate}
      />
    </Layout>
  );
};

export default Posts;
