import React, { useState } from "react";

import { Form, Button } from "react-bootstrap";
import { useForm } from "react-hook-form";
import { usePosts } from "../../utilities/context/postsContext";

export default function AddPostForm() {
  const { register, handleSubmit } = useForm();
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [userId, setUserId] = useState("");
  const { userOptions, addNewPost } = usePosts();

  const onSubmit = (e) => {
    e.preventDefault();
    const newPost = { title, body, userId: parseInt(userId) };
    addNewPost(newPost);
    setTitle("");
    setBody("");
    setUserId("");
  };

  return (
    <Form onSubmit={handleSubmit(onSubmit)}>
      <Form.Group controlId="formTitle">
        <Form.Label>Title</Form.Label>
        <Form.Control type="text" {...register("name")} />
      </Form.Group>

      <Form.Group controlId="formBody">
        <Form.Label>Body</Form.Label>
        <Form.Control type="text" {...register("body")} />
      </Form.Group>

      <Form.Group controlId="formUserId">
        <Form.Label>User</Form.Label>
        <Form.Control as="select" {...register("userId")}>
          <option value="">Select user...</option>
          {userOptions?.map((user) => (
            <option key={user.value} value={user.value}>
              {user.label}
            </option>
          ))}
        </Form.Control>
      </Form.Group>
      <Button className="mt-2" variant="primary" type="submit">
        Submit
      </Button>
    </Form>
  );
}
