import React, { useEffect, useState } from "react";
import Layout from "../components/layout/Layout";
import axios from "axios";
import { toast } from "react-hot-toast";
import { Button } from "react-bootstrap";

import Pagination from "../components/pagination/Pagination";
import usePagination from "../utilities/hooks/usePagination";
import ModalForDelete from "../components/modalsWindow/modalWindowForDelete/ModalWindowByDelete";
import FormForUpdatePost from "../components/formForUpdatePost/FormForUpdatePost";
import ModalWindowAddToFavorites from "../components/modalsWindow/addToFavoritesWindow/ModalWindowAddToFavorites";
import ModalWindowByDeleteAllCheckedPosts from "../components/modalsWindow/modalWindowForDelete/ModalWindowByDeleteAllCheckedPosts";
import CheckBox from "../components/postComponents/CheckBox";
import MyCard from "../components/postComponents/MyCard";

import styles from "./Posts.module.scss";
import { POST_API } from "../utilities/const/const";
import useDownloadData from "../utilities/hooks/useDownloadPosts";
import useDownloadPosts from "../utilities/hooks/useDownloadPosts";

const Posts = () => {
  const { posts, setPosts } = useDownloadPosts();

  // const [users, setUsers] = useState([]);
  // const [comments, setComments] = useState([]);
  const [isVisabilityModal, setIsVisabilityModal] = useState(false);
  const [isVisabilityModalOfFavorities, setIsVisabilityModalOfFavorities] =
    useState(false);
  const [
    isVisabilityModalOfDeleteCheckedPosts,
    setIsVisabilityModalOfDeleteCheckedPosts,
  ] = useState(false);
  const [selectedPostId, setSelectedPostId] = useState(null);
  const [selectedComments, setSelectedComments] = useState([]);
  const [selectedPostForComments, setSelectedPostForComments] = useState(null);
  const [editingPost, setEditingPost] = useState(null);
  const [editedPost, setEditedPost] = useState({
    title: "",
    body: "",
    userName: "",
  });
  const [favoritePosts, setFavoritePosts] = useState([]);
  const [selectedPosts, setSelectedPosts] = useState([]);

  const handleEdit = (post) => {
    setEditingPost(post.id);
    setEditedPost({
      title: post.title,
      body: post.body,
      userName: post.userName,
    });
  };

  const handleSave = async () => {
    try {
      const response = await axios.put(
        `https://jsonplaceholder.typicode.com/posts/${editingPost}`,
        editedPost
      );

      if (response.status === 200) {
        const updatedPosts = posts.map((post) =>
          post.id === editingPost ? response.data : post
        );
        setPosts(updatedPosts);
        toast.success("post updated");
      } else {
        toast.error("post update error");
      }
    } catch (error) {
      toast.error("post update error");
    }

    setEditingPost(null);
    setEditedPost({ title: "", body: "", userName: "" });
  };

  const handleCancel = () => {
    setEditingPost(null);
    setEditedPost({ title: "", body: "", userName: "" });
  };

  const {
    postsPerPage,
    changePage,
    setCurrentPage,
    currentPage,
    paginate,
    currentPosts,
  } = usePagination({ users, posts });

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/comments")
      .then((res) => setComments(res.data));
  }, []);
  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/users")
      .then((res) => setUsers(res.data));
  }, []);

  useEffect(() => {
    const savedFavorites = localStorage.getItem("favoritePosts");
    if (savedFavorites) {
      setFavoritePosts(JSON.parse(savedFavorites));
    } else {
      setFavoritePosts([]);
    }
  }, [selectedPosts]);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`https://jsonplaceholder.typicode.com/posts/${id}`);
      setPosts(posts.filter((post) => post.id !== id));
      toast.success("post deleted");
    } catch (error) {
      toast.error("post not deleted");
    }
  };
  const handleButtonClick = (postId) => {
    setSelectedPostId(postId);
  };

  const handleComment = (postId) => {
    if (selectedPostForComments === postId) {
      setSelectedPostForComments(null);
      setSelectedComments([]);
    } else {
      const commentsForPost = comments.filter(
        (comment) => comment.postId === postId
      );
      setSelectedComments(commentsForPost);
      setSelectedPostForComments(postId);
    }
  };

  const handleAddToFavorities = () => {
    const currentFavoritePosts =
      JSON.parse(localStorage.getItem("favoritePosts")) || [];
    const newFavoritePosts = [
      ...new Set([...currentFavoritePosts, ...selectedPosts]),
    ];
    localStorage.setItem("favoritePosts", JSON.stringify(newFavoritePosts));
    setFavoritePosts(newFavoritePosts);
    toast.success("posts added to favorites!");
  };

  const handleFavorite = (postId) => {
    if (favoritePosts.includes(postId)) {
      const newFavoritePosts = favoritePosts.filter((id) => id !== postId);
      setFavoritePosts(newFavoritePosts);
      localStorage.setItem("favoritePosts", JSON.stringify(newFavoritePosts));
      toast.error(" post deleted to favorites!");
    } else {
      const newFavoritePosts = [...favoritePosts, postId];
      setFavoritePosts(newFavoritePosts);
      localStorage.setItem("favoritePosts", JSON.stringify(newFavoritePosts));
      toast.success("post added to favorites!");
    }
  };

  const handleDeleteFromFavorities = async () => {
    const postsToDelete = [...selectedPosts];

    const deleteRequests = postsToDelete.map((postId) =>
      axios.delete(`https://jsonplaceholder.typicode.com/posts/${postId}`)
    );

    try {
      await Promise.all(deleteRequests);

      setPosts(posts.filter((post) => !postsToDelete.includes(post.id)));

      setFavoritePosts(
        favoritePosts.filter((postId) => !postsToDelete.includes(postId))
      );
      setSelectedPosts(
        selectedPosts.filter((postId) => !postsToDelete.includes(postId))
      );

      localStorage.setItem("favoritePosts", JSON.stringify(favoritePosts));

      toast.success("Selected posts removed");
    } catch (error) {
      toast.error("Failed to delete some posts");
    }
  };

  return (
    <Layout>
      <ModalForDelete
        id={selectedPostId}
        isVisabilityModal={isVisabilityModal}
        handleDelete={handleDelete}
        setIsVisabilityModal={setIsVisabilityModal}
      />
      <ModalWindowAddToFavorites
        handleAddToFavorities={handleAddToFavorities}
        isVisabilityModalOfFavorities={isVisabilityModalOfFavorities}
        setIsVisabilityModalOfFavorities={setIsVisabilityModalOfFavorities}
      />
      <ModalWindowByDeleteAllCheckedPosts
        setIsVisabilityModalOfDeleteCheckedPosts={
          setIsVisabilityModalOfDeleteCheckedPosts
        }
        isVisabilityModalOfDeleteCheckedPosts={
          isVisabilityModalOfDeleteCheckedPosts
        }
        handleDeleteFromFavorities={handleDeleteFromFavorities}
      />
      <CheckBox postsPerPage={postsPerPage} changePage={changePage} />
      <div className={styles.container}>
        {currentPosts.map((post) => {
          if (editingPost === post.id) {
            return (
              <div
                key={post.id}
                className="d-flex flex-column bg-light p-3 m-1 rounded"
              >
                <FormForUpdatePost
                  editedPost={editedPost}
                  setEditedPost={setEditedPost}
                  handleSave={handleSave}
                  handleCancel={handleCancel}
                />
              </div>
            );
          }

          return (
            <MyCard
              key={post.id}
              post={post}
              favoritePosts={favoritePosts}
              selectedComments={selectedComments}
              selectedPostForComments={selectedPostForComments}
              selectedPosts={selectedPosts}
              handleButtonClick={handleButtonClick}
              handleEdit={handleEdit}
              setSelectedPosts={setSelectedPosts}
              handleFavorite={handleFavorite}
              handleComment={handleComment}
              setIsVisabilityModal={setIsVisabilityModal}
            />
          );
        })}
      </div>
      {selectedPosts.length > 0 && (
        <div className="d-flex justify-content-center gap-5">
          <Button
            onClick={() => setIsVisabilityModalOfDeleteCheckedPosts(true)}
          >
            Delete
          </Button>
          <Button onClick={() => setIsVisabilityModalOfFavorities(true)}>
            Add
          </Button>
        </div>
      )}

      <Pagination
        somethingPerPage={postsPerPage}
        totalSomething={posts.length}
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
        paginate={paginate}
      />
    </Layout>
  );
};

export default Posts;
