import React from "react";
import { Button, Form } from "react-bootstrap";
import { useForm } from "react-hook-form";

const FormForUpdatePost = ({
  editedPost,
  setEditedPost,
  handleSavePost,
  handleCancel,
}) => {
  const {} = useForm({
    defaulValue: {
      ...editedPost,
    },
  });

  return (
    <>
      <Form>
        <Form.Group className="mb-3" controlId="formBasicUser">
          <Form.Label className="fw-bold fs-5">User</Form.Label>
          <Form.Control
            className="p-2"
            value={editedPost.userName}
            onChange={(e) =>
              setEditedPost({ ...editedPost, userName: e.target.value })
            }
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicTitle">
          <Form.Label className="fw-bold fs-5">Title</Form.Label>
          <Form.Control
            className="p-2"
            value={editedPost.title}
            onChange={(e) =>
              setEditedPost({ ...editedPost, title: e.target.value })
            }
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicBody">
          <Form.Label className="fw-bold fs-5">Description</Form.Label>
          <Form.Control
            className="p-2"
            as="textarea"
            value={editedPost.body}
            onChange={(e) =>
              setEditedPost({ ...editedPost, body: e.target.value })
            }
          />
        </Form.Group>
      </Form>
      <div className="d-flex gap-5 justify-content-center">
        <Button onClick={handleSavePost}>Save</Button>
        <Button onClick={handleCancel}>Cancel</Button>
      </div>
    </>
  );
};

export default FormForUpdatePost;
