import React from "react";

import { Layout } from "../components/layout/Layout";
import { useAlbums } from "../utilities/context/albumsContext";
import { useParams } from "react-router-dom";
import { Card, ListGroup } from "react-bootstrap";

const AlbumPhotos = () => {
  const { albumId } = useParams();
  const { albums } = useAlbums();
  const photos = albums.filter((album) => {
    if (album.id === +albumId) {
      return album;
    }
  });

  console.log("!!!!!!!", photos);
  return (
    <Layout>
      <h2 className="text-center">Album Photos</h2>
      <div>
        photos.map((photo) => (
        <Card.Img variant="top" src={photo.photos.thumbnailUrl} />
        <Card.Body>
          <Card.Title>{photo.photos.title}</Card.Title>
        </Card.Body>
        ))
      </div>
    </Layout>
  );
};

export default AlbumPhotos;
