import { POST_API } from "../const/const";
import { toast } from "react-hot-toast";
import axios from "axios";
import useDownloadPosts from "./useDownloadPosts";
export default function useSaveForm({
  setEditedPost,
  editedPost,

  setIsEdit,
}) {
  const { posts, setPosts } = useDownloadPosts();
  console.log(posts);
  const saveData = async (id) => {
    console.log("id", id);
    try {
      const response = await axios.put(`${POST_API}/${id}`, editedPost);

      if (response.status === 200) {
        const updatedPosts = posts.map((post) =>
          post.id === id ? response.data : post
        );
        setPosts(updatedPosts);
        toast.success("post updated");
      } else {
        toast.error("post update error");
        setIsEdit(false);
      }
    } catch (error) {
      toast.error("post update error");
    }

    setEditedPost({ title: "", body: "", userName: "" });
  };
  return { saveData };
}
