import React, { useState } from "react";

import { Layout } from "../components/layout/Layout";
import { useAlbums } from "../utilities/context/albumsContext";
import { useParams } from "react-router-dom";
import { Card, ListGroup } from "react-bootstrap";
import { ModalOfSelectPhoto } from "../components/albumsComponents/ModalOfSelectPhoto";

const PopupImage = ({ imageUrl, onClose }) => {
  return (
    <div className="popup-image">
      <div className="popup-image-overlay" onClick={onClose}></div>
      <div className="popup-image-content">
        <span className="popup-image-close" onClick={onClose}>
          &times;
        </span>
        <img src={imageUrl} alt="Full-size Image" />
      </div>
    </div>
  );
};
const AlbumPhotos = () => {
  const { albumId } = useParams();
  const { albums } = useAlbums();
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [showPhotoModal, setShowPhotoModal] = false;
  const photos = albums.filter((album) => {
    if (album.id === +albumId) {
      return album;
    }
  });
  console.log(selectedPhoto);
  const handlePhotoClick = (photoUrl) => {
    setSelectedPhoto(photoUrl);
  };
  const handleClosePopup = () => {
    setSelectedPhoto(null);
  };
  return (
    <Layout>
      <ModalOfSelectPhoto
        selectedPhoto={selectedPhoto}
        showPhotoModal={showPhotoModal}
      />
      <h2 className="text-center">Album Photos</h2>
      <div className="d-flex flex-wrap gap-3 justify-content-between ">
        {photos[0]?.photos.map((photo) => (
          <Card key={photo.id} style={{ width: "11rem" }}>
            <Card.Img
              variant="top"
              alt={photo.title}
              src={photo.thumbnailUrl}
              onClick={() => handlePhotoClick(photo.url)}
            />{" "}
            <Card.Body>
              <Card.Title>{photo.title}</Card.Title>
            </Card.Body>
            <ListGroup className="list-group-flush"></ListGroup>
          </Card>
        ))}
      </div>
      {selectedPhoto && (
        <PopupImage imageUrl={selectedPhoto} onClose={handleClosePopup} />
      )}
    </Layout>
  );
};

export default AlbumPhotos;
