import React from "react";
import { POST_API } from "../const/const";
import { toast } from "react-hot-toast";
import axios from "axios";

export default function useSaveForm({
  setEditedPost,
  setEditingPost,
  editedPost,
  editingPost,
  posts,
}) {
  const saveData = async () => {
    try {
      const response = await axios.put(`${POST_API}${editingPost}`, editedPost);

      if (response.status === 200) {
        const updatedPosts = posts.map((post) =>
          post.id === editingPost ? response.data : post
        );
        setPosts(updatedPosts);
        toast.success("post updated");
      } else {
        toast.error("post update error");
      }
    } catch (error) {
      toast.error("post update error");
    }

    setEditingPost(null);
    setEditedPost({ title: "", body: "", userName: "" });
  };
  return { saveData };
}
