import React, { useState } from "react";
import { Card, Form, FormCheck } from "react-bootstrap";
import {
  ButtonToAddToFavorites,
  DeleteButton,
  EditButton,
} from "../../utilities/const/Buttons";
import FormForUpdatePost from "../formForUpdatePost/FormForUpdatePost";

import styles from "../../pages/Posts.module.scss";
import { useFavoriteTodos } from "../../utilities/context/favoritiesTodosContext";

export const TodoCard = ({
  post,
  setSelectedTodos,
  setShowModalOfDelete,
  selectedTodos,
  setSelectedTodosId,
}) => {
  const { favoriteTodos, handleToggleAddfavoriteTodos } = useFavoriteTodos();

  const [isEdit, setIsEdit] = useState(false);
  const handleCloseEdit = () => setIsEdit(false);
  const [completed, setCompleted] = useState(post.completed);

  const handleToggleStatus = () => {
    const newCompleted = !completed;
    setCompleted(newCompleted);
  };

  const handleEdit = () => {
    setIsEdit(true);
  };

  const userMainData = {
    title: post?.title,
  };

  const todoTitleClass = completed ? styles.completed : "";

  if (isEdit) {
    return (
      <div
        key={post.id}
        className="d-flex flex-column bg-light p-3 m-1 rounded"
      >
        <FormForUpdatePost
          id={post?.id}
          editedPost={userMainData}
          handleCloseEdit={handleCloseEdit}
        />
      </div>
    );
  }

  return (
    <Card
      className={`text-center m-1 p-2 ${
        favoriteTodos.includes(post.id) ? "bg-warning" : "bg-light"
      }`}
    >
      <div>
        <Form.Check
          className={styles.check}
          checked={selectedTodos.includes(post.id)}
          onChange={(e) => {
            if (e.target.checked) {
              setSelectedTodos([...selectedTodos, post.id]);
            } else {
              setSelectedTodos(
                selectedTodos.filter((postId) => postId !== post.id)
              );
            }
          }}
        />

        <h3
          className={`d-flex justify-content-center gap-3 align-items-center ${todoTitleClass}`}
        >
          <FormCheck
            type="checkbox"
            checked={completed}
            onChange={handleToggleStatus}
            className="p-0 m-0"
          />
          {post?.title}
        </h3>
      </div>

      <div className="d-flex justify-content-center gap-5 ">
        <EditButton onClick={handleEdit} />
        <ButtonToAddToFavorites
          onClick={() => handleToggleAddfavoriteTodos(post.id)}
        />
        <DeleteButton
          onClick={() => {
            setSelectedTodosId(post.id);
            setShowModalOfDelete(true);
          }}
        />
      </div>
    </Card>
  );
};
