import { useEffect, useState } from "react";

export default function usePagination({ posts }) {
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setPostsPerPage] = useState(
    localStorage.getItem("quantityPosts") || 10
  );
  const [currentPosts, setCurrentPosts] = useState([]);

  useEffect(() => {
    const lastIndexPost = currentPage * postsPerPage;
    const firstIndexPost = lastIndexPost - postsPerPage;

    setCurrentPosts(posts.slice(firstIndexPost, lastIndexPost));
  }, [posts, currentPage, postsPerPage]);

  const paginate = (number) => setCurrentPage(number);
  const changePage = (value) => {
    setPostsPerPage(value);
    localStorage.setItem("quantityPosts", value);
  };

  return {
    postsPerPage,
    setCurrentPage,
    currentPage,
    paginate,
    changePage,
    currentPosts,
  };
}
