import React, { useState } from "react";

import { Layout } from "../components/layout/Layout";
import { useAlbums } from "../utilities/context/albumsContext";
import { useParams } from "react-router-dom";
import { Button, Card, ListGroup } from "react-bootstrap";

const AlbumPhotos = () => {
  const { albumId } = useParams();
  const { albums } = useAlbums();
  const [showBigPhoto, setShowBigPhoto] = useState(false);
  const photos = albums.filter((album) => {
    if (album.id === +albumId) {
      return album;
    }
  });

  return (
    <Layout>
      <h2 className="text-center">Album Photos</h2>
      <div className="d-flex flex-wrap gap-3 justify-content-between ">
        {photos[0]?.photos.map((photo) => (
          <Card key={photo.id} style={{ width: "15rem" }}>
            <Card.Img variant="top" src={photo.thumbnailUrl} />{" "}
            <Card.Body>
              <Card.Title>{photo.title}</Card.Title>
            </Card.Body>
            <ListGroup className="list-group-flush"></ListGroup>
            <Card.Footer className="d-flex ">
              <Button
                className="w-50 p-1 m-auto"
                onClick={() => {
                  setShowBigPhoto(true);
                }}
              >
                Enlarge photo
              </Button>
            </Card.Footer>
          </Card>
        ))}
      </div>
    </Layout>
  );
};

export default AlbumPhotos;
