import axios from "axios";
import { useEffect, useState } from "react";
import { ALBUMS_API, PHOTOS_API } from "../const/const";

export function useGetsPhoto() {
  const [albums, setAlbums] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  const getAlbumsLists = async () => {
    return await Promise.all([axios.get(ALBUMS_API), axios.get(PHOTOS_API)]);
  };

  useEffect(() => {
    setLoading(true);
    getPostList()
      .then(([albums, photos]) => {
        const photosMap = _.groupBy(photos.data, "albumId");
        const albumsWithPhotos = albums.data.map((album) => {
          return {
            ...albums,
            photo: photosMap[albums.albumId],
          };
        });

        setAlbums(albumsWithPhotos);
      })
      .catch((err) => setError(true))
      .finally(() => setLoading(false));
  }, []);
  return { albums };
  console.log(albums);
}
