import { Button, Modal } from "react-bootstrap/";

const ModalForDelete = ({
  isVisabilityModalOfDelete,
  setIsVisabilityModalOfDelete,
  handleDelete,
  id,
}) => {
  if (!isVisabilityModalOfDelete) {
    return null;
  }

  return (
    <div>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          zIndex: 1040,
        }}
      ></div>

      <div
        className="modal show"
        style={{ display: "block", position: "fixed", zIndex: 1050 }}
      >
        <Modal.Dialog>
          <Modal.Header
            closeButton
            onClick={() => setIsVisabilityModalOfDelete(false)}
          ></Modal.Header>
          <Modal.Body>
            <p className="text-center m-0">
              Are you sure you want to delete the post?
            </p>
          </Modal.Body>
          <Modal.Footer>
            <Button
              variant="secondary"
              onClick={() => {
                setIsVisabilityModalOfDelete(false);
              }}
            >
              NO
            </Button>
            <Button
              variant="primary"
              onClick={() => {
                handleDelete(id);
                setIsVisabilityModalOfDelete(false);
              }}
            >
              YES
            </Button>
          </Modal.Footer>
        </Modal.Dialog>
      </div>
    </div>
  );
};

export default ModalForDelete;
