import React, { useEffect, useState } from "react";
import Layout from "../components/layout/Layout";
import axios from "axios";
import { Button, Card, Form } from "react-bootstrap";
import Pagination from "../components/pagination/Pagination";

import styles from "./Posts.module.scss";
import usePagination from "../utilities/hooks/usePagination";
import { toast } from "react-hot-toast";
import ModalForDelete from "../components/modalWindowForDelete/ModalWindowByDelete";
import {
  ButtonToAddToFavorites,
  CommentButton,
  DeleteButton,
  EditButton,
} from "../utilities/const/Buttons";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);
  const [comments, setComments] = useState([]);
  const [isVisabilityModal, setIsVisabilityModal] = useState(false);
  const [selectedPostId, setSelectedPostId] = useState(null);
  const [selectedComments, setSelectedComments] = useState([]);
  const [selectedPostForComments, setSelectedPostForComments] = useState(null);
  const [editingPost, setEditingPost] = useState(null);
  const [editedTitle, setEditedTitle] = useState("");
  const [editedBody, setEditedBody] = useState("");
  const [editedUser, setEditedUser] = useState("");
  const [favoritePosts, setFavoritePosts] = useState(
    localStorage.getItem("favoritePosts")
  );

  const handleEdit = (post) => {
    setEditingPost(post.id);
    setEditedTitle(post.title);
    setEditedBody(post.body);
    setEditedUser(post.userName);
  };

  const handleSave = async () => {
    try {
      const updatedPost = {
        id: editingPost,
        title: editedTitle,
        body: editedBody,
        userName: editedUser,
      };
      const response = await axios.put(
        `https://jsonplaceholder.typicode.com/posts/${editingPost}`,
        updatedPost
      );

      if (response.status === 200) {
        const updatedPosts = posts.map((post) =>
          post.id === editingPost ? response.data : post
        );
        setPosts(updatedPosts);
        toast.success("пост обновлен");
      } else {
        toast.error("post update error");
      }
    } catch (error) {
      toast.error("post update error");
    }

    setEditingPost(null);
    setEditedTitle("");
    setEditedBody("");
    setEditedUser("");
  };

  const handleCancel = () => {
    setEditingPost(null);
    setEditedTitle("");
    setEditedBody("");
    setEditedUser("");
  };

  const {
    postsPerPage,
    changePage,
    setCurrentPage,
    currentPage,
    paginate,
    currentPosts,
  } = usePagination({ users, posts });

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((res) => setPosts(res.data));
  }, []);

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/comments")
      .then((res) => setComments(res.data));
  }, []);
  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/users")
      .then((res) => setUsers(res.data));
  }, []);
  useEffect(() => {
    const savedFavorites = localStorage.getItem("favoritePosts");
    if (savedFavorites) {
      setFavoritePosts(JSON.parse(savedFavorites));
    }
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`https://jsonplaceholder.typicode.com/posts/${id}`);
      setPosts(posts.filter((post) => post.id !== id));
      toast.success("post deleted");
    } catch (error) {
      toast.error("post not deleted");
    }
  };
  const handleButtonClick = (postId) => {
    setSelectedPostId(postId);
  };

  const handleComment = (postId) => {
    if (selectedPostForComments === postId) {
      setSelectedPostForComments(null);
      setSelectedComments([]);
    } else {
      const commentsForPost = comments.filter(
        (comment) => comment.postId === postId
      );
      setSelectedComments(commentsForPost);
      setSelectedPostForComments(postId);
    }
  };

  const handleFavorite = (postId) => {
    if (favoritePosts.includes(postId)) {
      const newFavoritePosts = favoritePosts.filter((id) => id !== postId);
      setFavoritePosts(newFavoritePosts);
      localStorage.setItem("favoritePosts", JSON.stringify(newFavoritePosts));
    } else {
      const newFavoritePosts = [...favoritePosts, postId];
      setFavoritePosts(newFavoritePosts);
      localStorage.setItem("favoritePosts", JSON.stringify(newFavoritePosts));
    }
  };

  return (
    <Layout>
      <ModalForDelete
        id={selectedPostId}
        isVisabilityModal={isVisabilityModal}
        handleDelete={handleDelete}
        setIsVisabilityModal={setIsVisabilityModal}
      />
      <div className="d-flex bg-white align-items-center p-3 m-1 rounded gap-2">
        <Button>Show</Button>
        <Form.Select
          aria-label="Default select example"
          className="w-auto"
          value={postsPerPage}
          onChange={(event) => changePage(event.target.value)}
        >
          <option value="10">10</option>
          <option value="20">20</option>
          <option value="50">50</option>
          <option value="100">100</option>
        </Form.Select>
      </div>
      <div className={styles.container}>
        {currentPosts.map((post) => {
          if (editingPost === post.id) {
            return (
              <div
                key={post.id}
                className="d-flex flex-column bg-light p-3 m-1 rounded"
              >
                <Form>
                  <Form.Group className="mb-3" controlId="formBasicUser">
                    <Form.Label className="fw-bold fs-5">User</Form.Label>
                    <Form.Control
                      className="p-2"
                      value={editedUser}
                      onChange={(e) => setEditedUser(e.target.value)}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formBasicTitle">
                    <Form.Label className="fw-bold fs-5">Title</Form.Label>
                    <Form.Control
                      className="p-2"
                      value={editedTitle}
                      onChange={(e) => setEditedTitle(e.target.value)}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formBasicBody">
                    <Form.Label className="fw-bold fs-5">
                      Description
                    </Form.Label>
                    <Form.Control
                      className="p-2"
                      as="textarea"
                      value={editedBody}
                      onChange={(e) => setEditedBody(e.target.value)}
                    />
                  </Form.Group>
                </Form>
                <div className="d-flex gap-5 justify-content-center">
                  <Button onClick={handleSave}>Save</Button>
                  <Button onClick={handleCancel}>Cancel</Button>
                </div>
              </div>
            );
          }

          return (
            <Card
              key={post.id}
              className={`text-center m-1 p-2 ${
                favoritePosts.includes(post.id) ? "bg-warning" : "bg-light"
              }`}
            >
              <div>
                <Form.Check className={styles.check} />
                <h3>{post.title}</h3>
                <h5>{post.userName}</h5>
                <p>{post.body}</p>
              </div>

              <div className="d-flex justify-content-center gap-5">
                <EditButton onClick={() => handleEdit(post)} />
                <ButtonToAddToFavorites
                  onClick={() => handleFavorite(post.id)}
                />
                <DeleteButton
                  onClick={() => {
                    handleButtonClick(post.id);
                    setIsVisabilityModal(true);
                  }}
                />
                <CommentButton
                  className={
                    selectedPostForComments === post.id
                      ? styles.commentButtonActive
                      : ""
                  }
                  onClick={() => {
                    handleComment(post.id);
                  }}
                />
              </div>
              {selectedPostForComments === post.id &&
                selectedComments.map((comments) => {
                  return (
                    <div
                      key={comments.id}
                      className="m-1 p-2 bg-info
                      rounded"
                    >
                      <h4>{comments.email}</h4>
                      <h5>{comments.name}</h5>
                      <p>{comments.body}</p>
                    </div>
                  );
                })}
            </Card>
          );
        })}
      </div>
      <Pagination
        somethingPerPage={postsPerPage}
        totalSomething={posts.length}
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
        paginate={paginate}
      />
    </Layout>
  );
};

export default Posts;
