import { useEffect, useState } from "react";

export function usePagination({ filteredPosts }) {
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setPostsPerPage] = useState(
    localStorage.getItem("quantityPosts") || 10
  );
  const [currentPosts, setCurrentPosts] = useState([]);

  useEffect(() => {
    const lastIndexPost = currentPage * postsPerPage;
    const firstIndexPost = lastIndexPost - postsPerPage;

    setCurrentPosts(filteredPosts.slice(firstIndexPost, lastIndexPost));
  }, [currentPage, postsPerPage]);

  const paginate = (number) => setCurrentPage(number);
  const changePage = (value) => {
    setPostsPerPage(value);
    localStorage.setItem("quantityPosts", value);
  };
  useEffect(() => {
    const handleStorageChange = () => {
      const updatedPostsPerPage = localStorage.getItem("quantityPosts");
      setPostsPerPage(updatedPostsPerPage);
    };

    window.addEventListener("storage", handleStorageChange);

    return () => {
      window.removeEventListener("storage", handleStorageChange);
    };
  }, []);

  return {
    postsPerPage,
    setCurrentPage,
    currentPage,
    paginate,
    changePage,
    currentPosts,
  };
}
