import React, { useState } from "react";
import {
  ButtonToAddToFavorites,
  DeleteButton,
  EditButton,
} from "../../utilities/const/Buttons";
import { Card, Form } from "react-bootstrap";
import { FormForUpdateAlbum } from "../formForUpdateAlbum/FormForUpdateAlbums";

import styles from "./PostsList.module.scss";
import { useFavoriteAlbums } from "../../utilities/context/favoritiesAlbumsContext";
import { Link } from "react-router-dom";

export const AlbumsCard = ({
  post,
  selectedAlbums,
  setSelectedAlbums,
  setSelectedAlbumsId,
  setShowModalOfDelete,
}) => {
  const { favoriteAlbums, handleToggleAddFavoriteAlbums } = useFavoriteAlbums();

  const [isEdit, setIsEdit] = useState(false);
  const handleCloseEdit = () => setIsEdit(false);

  const handleEdit = () => {
    setIsEdit(true);
  };
  const userMainData = {
    title: post?.title,
    userName: post?.user?.name,
  };
  if (isEdit) {
    return (
      <div
        key={post.id}
        className="d-flex flex-column bg-light p-3 m-1 rounded"
      >
        <FormForUpdateAlbum
          id={post?.id}
          editedPost={userMainData}
          handleCloseEdit={handleCloseEdit}
        />
      </div>
    );
  }

  return (
    <Card
      className={`text-center m-1 p-2 ${
        favoriteAlbums.includes(post.id) ? "bg-warning" : "bg-light"
      }`}
    >
      <div>
        <Form.Check
          className={styles.check}
          checked={selectedAlbums.includes(post.id)}
          onChange={(e) => {
            if (e.target.checked) {
              setSelectedAlbums([...selectedAlbums, post.id]);
            } else {
              setSelectedAlbums(
                selectedAlbums.filter((postId) => postId !== post.id)
              );
            }
          }}
        />

        <h3>
          <Link to={`/albums/${post.id}`}>{post?.title}</Link>
        </h3>
        <h5>{post?.user?.name}</h5>
      </div>
      <div className="d-flex justify-content-center gap-5">
        <EditButton onClick={handleEdit} />
        <ButtonToAddToFavorites
          onClick={() => handleToggleAddFavoriteAlbums(post.id)}
        />
        <DeleteButton
          onClick={() => {
            setSelectedAlbumsId(post.id);
            setShowModalOfDelete(true);
          }}
        />
      </div>
    </Card>
  );
};
