import { Modal } from "react-bootstrap/";

export const ModalOfSelectPhoto = ({
  showPhotoModal,
  setShowPhotoModal,
  selectedPhoto,
}) => {
  if (!showPhotoModal) {
    return null;
  }
  return (
    <div>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          zIndex: 1040,
        }}
      ></div>
      <div
        className="modal show"
        style={{ display: "block", position: "fixed", zIndex: 1050 }}
      >
        <Modal.Dialog>
          <Modal.Header
            closeButton
            onClick={() => setShowPhotoModal(false)}
          ></Modal.Header>
          <Modal.Body>
            <img src={selectedPhoto} alt="Full-size Image" />
          </Modal.Body>
        </Modal.Dialog>
      </div>
    </div>
  );
};
