import { Modal } from "react-bootstrap/";

export const ModalOfSelectPhoto = ({
  showPhotoModal,
  handleClosePopup,
  selectedPhoto,
}) => {
  if (!showPhotoModal) {
    return null;
  }

  const handleBackdropClick = (e) => {
    // Проверяем, что клик был совершен вне модального окна
    if (e.target.classList.contains("modal")) {
      handleClosePopup();
    }
  };

  return (
    <div>
      <div
        className="modal show"
        style={{
          display: "block",
          position: "fixed",
          zIndex: 1050,
        }}
        onClick={handleBackdropClick}
      >
        <Modal.Dialog>
          <Modal.Header closeButton onClick={handleClosePopup}></Modal.Header>
          <Modal.Body>
            <img
              src={selectedPhoto}
              alt="Full-size Image"
              style={{ width: "465px" }}
            />
          </Modal.Body>
        </Modal.Dialog>
      </div>
    </div>
  );
};
